import React, {useState} from "react";
import FullSequence from "../components/fullSequence";
import MagnifiedBox from "../components/MagnifiedBox";


function VisualizationPage(){
    const [selectedSequence, setSelectedSequence] = useState('');
    const handleSequenceSelect = (sequence) => {
        setSelectedSequence(sequence);
    };
    const inputSequence = '';

    return (
        <div>
            <p style={{ fontSize: '1.2rem', color: 'black', margin: '20px'}}>
                <div style ={{position: "relative"}}>
                    <FullSequence sequence={inputSequence} onSequenceSelect={handleSequenceSelect}/>
                </div>
                <div style ={{position: "fixed", top: '575px', left: '1000px'}}>
                    <MagnifiedBox sequence={selectedSequence}/>
                </div>
            </p>
        </div>
    );
};

export default VisualizationPage;
